class KyoQAToolApp(tk.Tk):
    """
    The main application class. It initializes the window, manages application
    state, and handles the communication between the UI and the backend processing thread.
    """
    def __init__(self):
        super().__init__()

        # --- State & UI Variables ---
        self.count_pass = tk.IntVar(value=0)
        self.count_fail = tk.IntVar(value=0)
        self.count_review = tk.IntVar(value=0)
        self.count_ocr = tk.IntVar(value=0)
        self.is_processing = False
        self.is_paused = False
        self.result_file_path = None
        self.reviewable_files = []
        self.start_time = None
        self.last_run_info = {}
        self.selected_folder = tk.StringVar()
        self.selected_excel = tk.StringVar()
        self.selected_files_list = []
        self.status_current_file = tk.StringVar(value="Ready to process")
        self.progress_value = tk.DoubleVar(value=0)
        self.time_remaining_var = tk.StringVar(value="")
        self.led_status_var = tk.StringVar(value="‚óè")
        self.is_fullscreen = True

        # --- Communication ---
        self.response_queue = queue.Queue()
        self.cancel_event = threading.Event()
        self.pause_event = threading.Event()

        # --- Load UI Assets ---
        self.icons = {name: self._load_icon_data(data) for name, data in ICON_DATA.items()}
        self.start_icon = self.icons.get("start.png")
        self.pause_icon = self.icons.get("pause.png")
        self.stop_icon = self.icons.get("stop.png")
        self.rerun_icon = self.icons.get("rerun.png")
        self.open_icon = self.icons.get("open.png")
        self.browse_icon = self.icons.get("browse.png")
        self.patterns_icon = self.icons.get("patterns.png")
        self.exit_icon = self.icons.get("exit.png")
        self.fullscreen_icon = self.icons.get("fullscreen.png")

        # --- Initialize UI ---
        self.style = ttk.Style(self)
        self._setup_window_styles()
        self._create_widgets()

        # Perform initial setup
        ensure_folders()
        self.attributes("-fullscreen", self.is_fullscreen)
        self.bind_all("<Escape>", self.toggle_fullscreen)

        self.after(100, self.process_response_queue)
        self.set_led("Ready")

    def _load_icon_data(self, data):
        """Loads a PhotoImage from base64 data."""
        try:
            image_data = base64.b64decode(data)
            return tk.PhotoImage(data=image_data)
        except Exception as e:
            logger.warning(f"Could not load icon data: {e}")
            return None

    def toggle_fullscreen(self, event=None):
        """Toggles the application between fullscreen and windowed mode."""
        self.is_fullscreen = not self.is_fullscreen
        self.attributes("-fullscreen", self.is_fullscreen)
        return "break"

    def _setup_window_styles(self):
        """Configures all the ttk styles for the modern UI."""
        self.title(f"Kyocera QA Knowledge Tool v{get_version()}")
        self.geometry("1200x900")
        self.minsize(1000, 800)
        self.protocol("WM_DELETE_WINDOW", self.on_closing)
        self.configure(bg=BRAND_COLORS["background"])
        self.columnconfigure(0, weight=1)
        self.rowconfigure(1, weight=1)

        self.style.theme_use("clam")
        self.style.configure("TFrame", background=BRAND_COLORS["background"])
        self.style.configure("TLabel", background=BRAND_COLORS["background"], font=("Segoe UI", 10))
        self.style.configure("TLabelFrame", background=BRAND_COLORS["background"], borderwidth=1, relief="groove")
        self.style.configure("TLabelFrame.Label", background=BRAND_COLORS["background"], font=("Segoe UI", 11, "bold"))
        self.style.configure("Header.TFrame", background=BRAND_COLORS["frame_background"])
        self.style.configure("TButton", font=("Segoe UI", 10), padding=6, relief="raised")
        self.style.map("TButton", background=[('active', '#e0e0e0')], foreground=[('!active', 'black')])
        self.style.configure("Red.TButton", font=("Segoe UI", 12, "bold"), foreground="white")
        self.style.map("Red.TButton", background=[('active', '#A81F14'), ('!active', BRAND_COLORS["kyocera_red"])])
        self.style.configure("Status.TFrame", background=BRAND_COLORS["status_default_bg"], relief="sunken", borderwidth=1)
        self.style.configure("Status.TLabel", font=("Segoe UI", 10))
        self.style.configure("Status.Header.TLabel", font=("Segoe UI", 10, "bold"))
        self.style.configure("LED.TLabel", font=("Segoe UI", 16))
        self.style.configure("Count.Green.TLabel", foreground=BRAND_COLORS["success_green"], font=("Segoe UI", 10, "bold"))
        self.style.configure("Count.Red.TLabel", foreground=BRAND_COLORS["fail_red"], font=("Segoe UI", 10, "bold"))
        self.style.configure("Count.Orange.TLabel", foreground=BRAND_COLORS["warning_orange"], font=("Segoe UI", 10, "bold"))
        self.style.configure("Count.Blue.TLabel", foreground=BRAND_COLORS["accent_blue"], font=("Segoe UI", 10, "bold"))
        self.style.configure("Blue.Horizontal.TProgressbar", background=BRAND_COLORS["accent_blue"])

    def _create_widgets(self):
        """Creates and places all UI widgets using the gui_components module."""
        create_main_header(self, get_version(), BRAND_COLORS)
        main_frame = ttk.Frame(self, padding=20)
        main_frame.grid(row=1, column=0, sticky="nsew")
        main_frame.columnconfigure(0, weight=1)
        main_frame.rowconfigure(2, weight=1)
        create_io_section(main_frame, self)
        create_process_controls(main_frame, self)
        create_status_and_log_section(main_frame, self)
        self.log_text.tag_configure("timestamp", foreground="grey")
        self.log_text.tag_configure("info_fg", foreground="#00529B")
        self.log_text.tag_configure("warning_fg", foreground="#9F6000")
        self.log_text.tag_configure("error_fg", foreground="#D8000C")
        self.log_text.tag_configure("success_fg", foreground="#4F8A10")

    def log_message(self, message, level="info"):
        """Logs a message to the UI's text widget and the main log file."""
        timestamp = time.strftime("%H:%M:%S")
        self.log_text.config(state=tk.NORMAL)
        self.log_text.insert(tk.END, f"[{timestamp}] ", "timestamp")
        self.log_text.insert(tk.END, f"{message}\n", f"{level}_fg")
        self.log_text.see(tk.END)
        self.log_text.config(state=tk.DISABLED)
        logger.log(logging.INFO if level != "error" else logging.ERROR, message)

    def start_processing(self, job=None, is_rerun=False):
        """Starts the main processing job in a new thread."""
        if self.is_processing: return
        
        # FIXED: Consolidate file list logic here to be the single source of truth.
        if not job:
            input_files = []
            # Get files from individual selection
            if self.selected_files_list:
                input_files.extend(self.selected_files_list)
            # Get files from folder selection (recursively)
            folder_path = self.selected_folder.get()
            if folder_path:
                input_files.extend(list(Path(folder_path).rglob("*.pdf")))
            
            # Validate that we have files to process
            if not input_files:
                messagebox.showwarning("Input Missing", "Please select one or more PDF files or a folder containing PDFs.")
                return

            excel_path = self.selected_excel.get()
            if not excel_path:
                messagebox.showwarning("Input Missing", "Please select a base Excel file to update.")
                return
            
            job = {"excel_path": excel_path, "input_path": [str(p) for p in input_files]}
            self.last_run_info = job

        job["is_rerun"] = is_rerun
        self.update_ui_for_start()
        self.log_message(f"Starting processing job with {len(job['input_path'])} file(s)...", "info")
        self.start_time = time.time()
        threading.Thread(target=run_processing_job, args=(job, self.response_queue, self.cancel_event, self.pause_event), daemon=True).start()

    def rerun_flagged_job(self):
        """Starts a new job specifically for files that were flagged for review."""
        if not self.reviewable_files:
            messagebox.showwarning("No Files", "There are no files flagged for re-running.")
            return
        if not self.result_file_path:
            messagebox.showerror("Error", "The previous result file path was not found. Cannot re-run.")
            return
        files_to_rerun = [item["pdf_path"] for item in self.reviewable_files]
        self.log_message(f"Re-running {len(files_to_rerun)} flagged files...", "info")
        self.start_processing(job={"excel_path": self.result_file_path, "input_path": files_to_rerun}, is_rerun=True)

    def browse_excel(self):
        """Opens a dialog to select the master Excel file."""
        path = filedialog.askopenfilename(title="Select Excel Template", filetypes=[("Excel Files", "*.xlsx *.xlsm"), ("All Files", "*.*")])
        if path:
            self.selected_excel.set(path)
            self.log_message(f"Excel template selected: {Path(path).name}", "info")

    def browse_folder(self):
        """Opens a dialog to select a folder of PDFs."""
        path = filedialog.askdirectory(title="Select Folder with PDFs")
        if path:
            self.selected_folder.set(path)
            self.selected_files_list = [] # Clear individual file selection
            # FIXED: Scan recursively and update the UI label with the correct count.
            pdf_count = len(list(Path(path).rglob("*.pdf")))
            self.files_label.config(text=f"{pdf_count} PDFs found in folder (and subfolders)")
            self.log_message(f"Folder selected: {path} ({pdf_count} PDFs found)", "info")

    def browse_files(self):
        """Opens a dialog to select one or more individual PDF files."""
        paths = filedialog.askopenfilenames(title="Select PDF Files", filetypes=[("PDF Files", "*.pdf"), ("All Files", "*.*")])
        if paths:
            self.selected_files_list = list(paths)
            self.selected_folder.set("") # Clear folder selection
            self.files_label.config(text=f"{len(paths)} individual files selected")
            self.log_message(f"{len(paths)} PDF files selected", "info")

    def toggle_pause(self):
        """Pauses or resumes the current processing job."""
        if not self.is_processing: return
        self.is_paused = not self.is_paused
        if self.is_paused:
            self.pause_event.set()
            self.pause_btn.config(text=" Resume")
            self.log_message("Processing paused.", "warning")
            self.set_led("Paused")
        else:
            self.pause_event.clear()
            self.pause_btn.config(text=" Pause")
            self.log_message("Processing resumed.", "info")
            self.set_led("Processing")

    def stop_processing(self):
        """Stops the current processing job."""
        if not self.is_processing: return
        if messagebox.askyesno("Confirm Stop", "Are you sure you want to stop the current processing job?"):
            self.cancel_event.set()
            self.log_message("Stopping processing...", "warning")
            self.set_led("Stopping")

    def on_closing(self):
        """Handles the application close event."""
        if self.is_processing:
            if not messagebox.askyesno("Exit Confirmation", "A processing job is currently running. Are you sure you want to exit?"):
                return
        logger.info("Closing application and cleaning up temporary files.")
        self.cancel_event.set()
        cleanup_temp_files()
        self.destroy()

    def open_result(self):
        """Opens the generated Excel report file."""
        if self.result_file_path and Path(self.result_file_path).exists():
            try:
                open_file(self.result_file_path)
                self.log_message(f"Opened result file: {Path(self.result_file_path).name}", "info")
            except Exception as e:
                messagebox.showerror("Error Opening File", f"Could not open the file:\n{e}")
        else:
            messagebox.showwarning("File Not Found", "The result file was not found. It may have been moved or deleted.")

    def open_pattern_manager(self):
        """Opens the pattern management tool."""
        file_info = self.reviewable_files[0] if self.reviewable_files else None
        ReviewWindow(self, "MODEL_PATTERNS", "Model Patterns", file_info)

    def open_review_for_selected_file(self):
        """Opens the pattern manager for a specific file selected in the review list."""
        selection = self.review_tree.selection()
        if not selection:
            messagebox.showwarning("No Selection", "Please select a file from the 'Files to Review' list.")
            return
        item_id = selection[0]
        filename = self.review_tree.item(item_id, "values")[0]
        review_info = next((f for f in self.reviewable_files if f.get('filename') == filename), None)
        if review_info:
            ReviewWindow(self, "MODEL_PATTERNS", "Model Patterns", review_info)
        else:
            messagebox.showerror("Error", "Could not find the review information for the selected file.")

    def set_led(self, status):
        """Updates the color of the status LED and background based on the current status."""
        led_config = {
            "Ready": ("#107C10", BRAND_COLORS["status_default_bg"]),
            "Processing": (BRAND_COLORS["accent_blue"], BRAND_COLORS["status_processing_bg"]),
            "OCR": (BRAND_COLORS["accent_blue"], BRAND_COLORS["status_ocr_bg"]),
            "AI": (BRAND_COLORS["accent_blue"], BRAND_COLORS["status_ai_bg"]),
            "Paused": (BRAND_COLORS["warning_orange"], BRAND_COLORS["status_default_bg"]),
            "Stopping": (BRAND_COLORS["fail_red"], BRAND_COLORS["status_default_bg"]),
            "Error": (BRAND_COLORS["fail_red"], BRAND_COLORS["status_default_bg"]),
            "Complete": ("#107C10", BRAND_COLORS["status_default_bg"]),
            "Queued": ("grey", BRAND_COLORS["status_default_bg"]),
            "Saving": ("#107C10", BRAND_COLORS["status_default_bg"]),
        }
        color, bg_color = led_config.get(status, ("grey", BRAND_COLORS["status_default_bg"]))
        self.led_label.config(foreground=color)
        self.status_frame.config(style="Status.TFrame")
        self.style.configure("Status.TFrame", background=bg_color)
        for child in self.status_frame.winfo_children():
            child.configure(style="Status.TLabel")
        self.style.configure("Status.TLabel", background=bg_color)

    def update_ui_for_start(self):
        """Disables/enables widgets when a job starts."""
        self.is_processing = True
        self.is_paused = False
        self.cancel_event.clear()
        self.pause_event.clear()
        for var in [self.count_pass, self.count_fail, self.count_review, self.count_ocr]: var.set(0)
        self.reviewable_files.clear()
        self.review_tree.delete(*self.review_tree.get_children())
        self.process_btn.config(state=tk.DISABLED)
        self.pause_btn.config(state=tk.NORMAL, text=" Pause")
        self.stop_btn.config(state=tk.NORMAL)
        self.review_btn.config(state=tk.DISABLED)
        self.open_result_btn.config(state=tk.DISABLED)
        self.exit_btn.config(state=tk.DISABLED)
        self.rerun_btn.config(state=tk.DISABLED)
        self.review_file_btn.config(state=tk.DISABLED)
        self.status_current_file.set("Initializing...")
        self.time_remaining_var.set("Calculating...")
        self.progress_value.set(0)
        self.set_led("Processing")

    def update_ui_for_finish(self, status):
        """Disables/enables widgets when a job finishes."""
        self.is_processing = False
        self.is_paused = False
        self.process_btn.config(state=tk.NORMAL)
        self.pause_btn.config(state=tk.DISABLED, text=" Pause")
        self.stop_btn.config(state=tk.DISABLED)
        self.exit_btn.config(state=tk.NORMAL)
        self.review_btn.config(state=tk.NORMAL)
        if self.result_file_path: self.open_result_btn.config(state=tk.NORMAL)
        if self.reviewable_files: self.rerun_btn.config(state=tk.NORMAL)
        final_status = "Complete" if status == "Complete" else "Error"
        self.status_current_file.set(f"Job {status}")
        self.time_remaining_var.set("Done!")
        self.set_led(final_status)
        self.progress_value.set(100)

    def update_progress(self, current, total):
        """Updates the progress bar and time remaining estimate."""
        if total > 0:
            percent = (current / total) * 100
            self.progress_value.set(percent)
            if self.start_time and current > 1:
                elapsed = time.time() - self.start_time
                rate = current / elapsed
                remaining = (total - current) / rate if rate > 0 else 0
                if remaining > 60:
                    self.time_remaining_var.set(f"~{int(remaining/60)}m {int(remaining%60)}s left")
                else:
                    self.time_remaining_var.set(f"~{int(remaining)}s left")

    def process_response_queue(self):
        """
        This is the core message handler. It checks the queue for
        messages from the backend thread and updates the UI accordingly.
        """
        try:
            while not self.response_queue.empty():
                msg = self.response_queue.get_nowait()
                mtype = msg.get("type")

                if mtype == "log":
                    self.log_message(msg.get("msg", ""), msg.get("tag", "info"))
                elif mtype == "status":
                    self.status_current_file.set(msg.get("msg", ""))
                    if "led" in msg: self.set_led(msg["led"])
                elif mtype == "progress":
                    self.update_progress(msg.get("current", 0), msg.get("total", 1))
                elif mtype == "increment_counter":
                    counter_name = f"count_{msg.get('counter')}"
                    if hasattr(self, counter_name):
                        var = getattr(self, counter_name)
                        var.set(var.get() + 1)
                elif mtype == "file_complete":
                    status_key = msg.get('status', 'Fail').lower().replace(' ', '_')
                    counter_name = f"count_{status_key}"
                    if hasattr(self, counter_name):
                        var = getattr(self, counter_name)
                        var.set(var.get() + 1)
                elif mtype == "review_item":
                    data = msg.get("data", {})
                    if data:
                        self.reviewable_files.append(data)
                        self.review_tree.insert('', 'end', values=(data.get('filename', 'Unknown'),))
                elif mtype == "result_path":
                    self.result_file_path = msg.get("path")
                elif mtype == "finish":
                    status = msg.get("status", "Complete")
                    elapsed = time.time() - self.start_time if self.start_time else 0
                    self.log_message(f"Job finished: {status} (Time: {int(elapsed/60)}m {int(elapsed%60)}s)", "success" if status == "Complete" else "error")
                    self.update_ui_for_finish(status)
        except queue.Empty:
            pass
        except Exception as e:
            self.log_message(f"Critical error processing UI queue: {e}", "error")
            logger.exception("An unhandled error occurred in the UI queue processor.")
        
        self.after(100, self.process_response_queue)

if __name__ == "__main__":
    try:
        # This check is important for when the app is packaged with PyInstaller
        if not getattr(sys, 'frozen', False):
            # Add the project root to the Python path to ensure modules are found
            project_root = Path(__file__).parent
            sys.path.insert(0, str(project_root))

        app = KyoQAToolApp()
        app.mainloop()
    except Exception as e:
        import traceback
        logger.critical(f"Failed to start application: {e}\n{traceback.format_exc()}")
        messagebox.showerror("Fatal Error", f"A fatal error occurred on startup:\n\n{e}")
